public class Euler8 {
	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String fack = "";
		long a, b, c, d, e, f, g, h, ias, j, k, l, m, sum, end=0;
		for (int i = 0; i < fack.length()-12; i++) {
			a = (long)fack.charAt(i)-48;
			b = (long)fack.charAt(i+1)-48;
			c = (long)fack.charAt(i+2)-48;
			d = (long)fack.charAt(i+3)-48;
			e = (long)fack.charAt(i+4)-48;
			f = (long)fack.charAt(i+5)-48;
			g = (long)fack.charAt(i+6)-48;
			h = (long)fack.charAt(i+7)-48;
			ias = (long)fack.charAt(i+8)-48;
			j = (long)fack.charAt(i+9)-48;
			k = (long)fack.charAt(i+10)-48;
			l = (long)fack.charAt(i+11)-48;
			m = (long)fack.charAt(i+12)-48;
			sum = a*b*c*d*e*f*g*h*ias*j*k*l*m;
			
			if (sum > end) {
				end = sum;
			}	
		}
		System.out.println(end);		
	}
}